package com.algorithm.day6;

import org.junit.Test;

import java.util.HashMap;
import java.util.Map;
import java.util.Objects;

public class PermutationInAString {
    public boolean checkInclusion(String s1, String s2) {
        if(s1.isEmpty() && s2.isEmpty())
            return true;
        if(s1.length()>s2.length())
            return false;

        Map<Character,Integer> s1Freq = new HashMap<>();
        Map<Character,Integer> s2SubstrFreq = new HashMap<>();

        setup(s1Freq,s1);
        setup(s2SubstrFreq,s2.substring(0,s1.length()));

        int right;
        for(int left=0;left<=s2.length()-s1.length();left++) {
            if(s1Freq.containsKey(s2.charAt(left))) {
                boolean isAnagram = isAnagram(s1Freq,s2SubstrFreq);
                if(isAnagram)
                    return true;
            }
            right=left+s1.length();
            if(right<s2.length())
                s2SubstrFreq.put(s2.charAt(right), s2SubstrFreq.getOrDefault(s2.charAt(right),0)+1);
            s2SubstrFreq.replace(s2.charAt(left), s2SubstrFreq.get(s2.charAt(left))-1);
        }
        return false;
    }

    boolean isAnagram(Map<Character,Integer> s1Freq,Map<Character,Integer> s2SubstrFreq) {
        for(char s1Char : s1Freq.keySet())
            if(!s2SubstrFreq.containsKey(s1Char) || !Objects.equals(s2SubstrFreq.get(s1Char), s1Freq.get(s1Char)))
                return false;
        return true;
    }

    void setup(Map<Character,Integer> sFreq,String s) {
        for(int i=0;i<s.length();i++) {
            sFreq.put(s.charAt(i),sFreq.getOrDefault(s.charAt(i),0)+1);
        }
    }

    @Test
    public void test1(){
        String a ="";
        String b="";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test2(){
        String a ="ab";
        String b="ab";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test3(){
        String a ="ab";
        String b="ba";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test4(){
        String a ="ab";
        String b="abs";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test5(){
        String a ="ab";
        String b="vab";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test6(){
        String a ="ab";
        String b="vba";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test7(){
        String a ="an";
        String b="ahnj";
        System.out.println(checkInclusion(a,b));
        assert !checkInclusion(a,b);
    }
    @Test
    public void test8(){
        String a ="am";
        String b="hakimjnam";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test9(){
        String a ="ab";
        String b="bnhuhna";
        System.out.println(checkInclusion(a,b));
        assert !checkInclusion(a,b);
    }
    @Test
    public void test10(){
        String a ="ab";
        String b="bagsfdrabfgbgfa";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
    @Test
    public void test11(){
        String a ="";
        String b="";
        System.out.println(checkInclusion(a,b));
        assert checkInclusion(a,b);
    }
}
